package com.obpoo.gfsfabricsoftware.PurchaseOrder.Adapter;import android.app.Activity;import android.content.Intent;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.Gravity;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.obpoo.gfsfabricsoftware.PurchaseOrder.UI.POAdd;import com.obpoo.gfsfabricsoftware.PurchaseOrder.UI.Search_Select;import com.obpoo.gfsfabricsoftware.R;import com.obpoo.gfsfabricsoftware.user.datamodels.UserDetail;import com.obpoo.gfsfabricsoftware.utilities.AppConstants;import com.obpoo.gfsfabricsoftware.vendors.datamodels.VendorsDetail;import java.util.ArrayList;import butterknife.BindView;import butterknife.ButterKnife;public class SearchItemAdapter extends RecyclerView.Adapter<SearchItemAdapter.ViewHolder> {    Activity context;    ArrayList<VendorsDetail> vendorData;    ArrayList<UserDetail> userList;    String tag;    public void updateVendorList(ArrayList<VendorsDetail> list){        vendorData = list;        notifyDataSetChanged();    }    public void updateStaffList(ArrayList<UserDetail> list){        userList = list;        notifyDataSetChanged();    }    public SearchItemAdapter(Search_Select search_select, ArrayList<VendorsDetail> vendorData, ArrayList<UserDetail> userList, String tag) {        context = search_select;        this.vendorData = vendorData;        this.userList = userList;        this.tag = tag;    }    @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        View view = LayoutInflater.from(context).inflate(R.layout.searchall_item, viewGroup, false);        SearchItemAdapter.ViewHolder rootView = new SearchItemAdapter.ViewHolder(view);        return rootView;    }    @Override    public void onBindViewHolder(@NonNull final ViewHolder viewHolder, final int i) {        viewHolder.item_name.setGravity(Gravity.CENTER_VERTICAL);        if (tag.equals("staff")) {            viewHolder.item_name.setText(userList.get(i).getName());        } else if (tag.equals("vendor")) {            viewHolder.item_name.setText(vendorData.get(i).getVendorno());        }        viewHolder.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = new Intent(context, POAdd.class);                if (tag.equals("staff")) {                    intent.putExtra("selectstaffNo", userList.get(i).getId());                    intent.putExtra("selectstaffName", userList.get(i).getName());                    context.setResult( AppConstants.selectSearchStaff,intent);                    context.finish();                } else if (tag.equals("vendor")) {                    intent.putExtra("selectVendorNo", vendorData.get(i).getVendorno());                    intent.putExtra("selectVendorid", vendorData.get(i).getVendorID());                    context.setResult(AppConstants.selectSearchVendor,intent);                    context.finish();                }            }        });    }    @Override    public int getItemCount() {        int size = 0;        if (tag.equals("vendor")) {            size = vendorData.size();        } else if (tag.equals("staff")) {            size = userList.size();        }        return size;    }    class ViewHolder extends RecyclerView.ViewHolder {        @BindView(R.id.item_name)        TextView item_name;        public ViewHolder(@NonNull View itemView) {            super(itemView);            ButterKnife.bind(this, itemView);        }    }}