package com.obpoo.gfsfabricsoftware.PurchaseOrder.UI;import android.content.Intent;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.support.v7.widget.Toolbar;import android.text.Editable;import android.text.TextWatcher;import android.util.Log;import android.widget.EditText;import com.obpoo.gfsfabricsoftware.PurchaseOrder.Adapter.SearchItemAdapter;import com.obpoo.gfsfabricsoftware.PurchaseOrder.DataModel.ViewPOModel.poDatum;import com.obpoo.gfsfabricsoftware.R;import com.obpoo.gfsfabricsoftware.Report.Adapter.PoDetailAdapter;import com.obpoo.gfsfabricsoftware.Report.UI.PoDetailReport;import com.obpoo.gfsfabricsoftware.ui.activities.BaseActivity;import com.obpoo.gfsfabricsoftware.user.datamodels.UserDetail;import com.obpoo.gfsfabricsoftware.utilities.AppConstants;import com.obpoo.gfsfabricsoftware.vendors.datamodels.VendorsDetail;import java.util.ArrayList;import butterknife.BindView;import butterknife.ButterKnife;public class Search_Select extends BaseActivity {    @BindView(R.id.recycler_view)    RecyclerView recycler_view;    @BindView(R.id.etSearch)    EditText etSearch;    SearchItemAdapter adapter;    private ArrayList<VendorsDetail> vendorData = new ArrayList<>();    ArrayList<UserDetail> userList = new ArrayList<>();    String tag;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_search__select);        ButterKnife.bind(this);        Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);        toolbar.setTitle("Create Request");        setSupportActionBar(toolbar);        enableActionBar(true);        LinearLayoutManager lm = new LinearLayoutManager(this);        lm.setOrientation(LinearLayoutManager.VERTICAL);        recycler_view.setLayoutManager(lm);        vendorData = getIntent().getParcelableArrayListExtra("vendorlist");        userList = getIntent().getParcelableArrayListExtra("userList");        tag = getIntent().getStringExtra("tag");        adapter = new SearchItemAdapter(Search_Select.this, vendorData, userList, tag);        recycler_view.setAdapter(adapter);        etSearch.addTextChangedListener(new TextWatcher() {            @Override            public void beforeTextChanged(CharSequence charSequence, int i, int i1, int i2) {            }            @Override            public void onTextChanged(CharSequence charSequence, int i, int i1, int i2) {            }            @Override            public void afterTextChanged(Editable editable) {                if (tag.equals("vendor")) {                    if (vendorData != null) {                        filter(editable.toString());                    }                }                if (tag.equals("staff")) {                    if (userList != null) {                        filter(editable.toString());                    }                }            }        });    }    void filter(String text) {        if (tag.equals("vendor")) {            ArrayList<VendorsDetail> temp = new ArrayList();            for (VendorsDetail d : vendorData) {                if (d.getVendorno().toLowerCase().contains(text.toLowerCase())) {                    temp.add(d);                }            }            adapter.updateVendorList(temp);        }        if (tag.equals("staff")) {            ArrayList<UserDetail> temp = new ArrayList();            for (UserDetail d : userList) {                if (d.getName().toLowerCase().contains(text.toLowerCase())) {                    temp.add(d);                }            }            adapter.updateStaffList(temp);        }    }}