package com.obpoo.gfsfabricsoftware.Report.Adapter;import android.app.Activity;import android.content.Intent;import android.support.annotation.NonNull;import android.support.v7.widget.CardView;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.obpoo.gfsfabricsoftware.PurchaseOrder.Adapter.ConfirmPOAdapter;import com.obpoo.gfsfabricsoftware.PurchaseOrder.DataModel.ViewPOModel.poDatum;import com.obpoo.gfsfabricsoftware.PurchaseOrder.DataModel.ViewPOModel.poItem;import com.obpoo.gfsfabricsoftware.PurchaseOrder.UI.ViewDetails;import com.obpoo.gfsfabricsoftware.R;import com.obpoo.gfsfabricsoftware.Report.DataModel.PurchaseOrderDetails.PoDetailsItems;import com.obpoo.gfsfabricsoftware.Report.DataModel.PurchaseOrderDetails.PoDetailsRespoDet;import com.obpoo.gfsfabricsoftware.Report.UI.PoDetailReport;import com.obpoo.gfsfabricsoftware.Report.UI.ViewDetailsReportPO;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.ArrayList;import butterknife.BindView;import butterknife.ButterKnife;public class PoDetailAdapter extends RecyclerView.Adapter<PoDetailAdapter.ViewHolder> {    Activity context;    ArrayList<PoDetailsRespoDet> data;    public PoDetailAdapter(Activity context, ArrayList<PoDetailsRespoDet> data) {        this.data = data;        this.context = context;    }    @NonNull    @Override    public PoDetailAdapter.ViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        View view = LayoutInflater.from(context).inflate(R.layout.confirm_po_adapter, viewGroup, false);        PoDetailAdapter.ViewHolder root = new PoDetailAdapter.ViewHolder(view);        return root;    }    @Override    public void onBindViewHolder(@NonNull final PoDetailAdapter.ViewHolder viewHolder, final int i) {        final ArrayList<PoDetailsItems> items = data.get(i).getItems();        int itmsize = items.size();        viewHolder.poNumCM.setVisibility(View.GONE);        viewHolder.id.setText("#" + data.get(i).getId());        viewHolder.factoryname.setText(data.get(i).getFactory());        viewHolder.status.setText(data.get(i).getStatusText());        viewHolder.item.setText("Item:" + itmsize);        try {            viewHolder.date.setText(new SimpleDateFormat("dd-MMM-yyyy").format(new SimpleDateFormat("dd-MM-yyyy").parse(data.get(i).getDuedate())));            if (data.get(i).getDeliveryDate().equals("")) {                viewHolder.deliver_date.setText("N/A");            } else {                viewHolder.deliver_date.setText(new SimpleDateFormat("dd-MMM-yyyy").format(new SimpleDateFormat("dd-MM-yyyy").parse(data.get(i).getDeliveryDate())));            }        } catch (ParseException e) {            e.printStackTrace();        }        viewHolder.item.setText("Item:" + itmsize);        viewHolder.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent in = new Intent(context, ViewDetailsReportPO.class);                in.putExtra("id", data.get(i).getId());                in.putExtra("factory", viewHolder.factoryname.getText());                in.putExtra("status", viewHolder.status.getText());                in.putExtra("due_date", viewHolder.date.getText());                in.putExtra("deliver_date", viewHolder.deliver_date.getText());                in.putExtra("create_date", data.get(i).getCreatedOn());                in.putExtra("update", data.get(i).getUpdatedOn());                in.putExtra("staf", data.get(i).getStaff());                in.putExtra("cc_mail", data.get(i).getCcEmail());                in.putExtra("fac_mail", data.get(i).getFactoryEmail());                in.putParcelableArrayListExtra("item", items);                context.startActivity(in);            }        });    }    @Override    public int getItemCount() {        return data.size();    }    class ViewHolder extends RecyclerView.ViewHolder {        @BindView(R.id.id)        TextView id;        @BindView(R.id.item)        TextView item;        @BindView(R.id.factoryname)        TextView factoryname;        @BindView(R.id.status)        TextView status;        @BindView(R.id.date)        TextView date;        @BindView(R.id.deliver_date)        TextView deliver_date;        @BindView(R.id.indc)        CardView indc;        @BindView(R.id.price_img)        ImageView price_img;        @BindView(R.id.go)        ImageView go;        @BindView(R.id.price)        TextView price;        @BindView(R.id.poNumCM)        TextView poNumCM;        public ViewHolder(@NonNull View itemView) {            super(itemView);            ButterKnife.bind(this, itemView);        }    }}