package com.obpoo.gfsfabricsoftware.Report.Adapter;import android.content.Context;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.obpoo.gfsfabricsoftware.R;import com.obpoo.gfsfabricsoftware.Report.DataModel.PurchaseOrderDetails.PoDetailsItems;import com.obpoo.gfsfabricsoftware.Report.UI.ViewDetailsReportPO;import java.util.ArrayList;import butterknife.BindView;import butterknife.ButterKnife;public class ViewDetailRPOAdapter extends RecyclerView.Adapter<ViewDetailRPOAdapter.ViewHolder> {    Context context;    ArrayList<PoDetailsItems> items;    public ViewDetailRPOAdapter(ViewDetailsReportPO context, ArrayList<PoDetailsItems> items) {        this.context = context;        this.items = items;    }    @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        View view = LayoutInflater.from(context).inflate(R.layout.viewdetailitem_report, viewGroup, false);        ViewDetailRPOAdapter.ViewHolder rootView = new ViewDetailRPOAdapter.ViewHolder(view);        return rootView;    }    @Override    public void onBindViewHolder(@NonNull ViewHolder viewHolder, int i) {        PoDetailsItems item = items.get(i);        viewHolder.item_code.setText(item.getArtNoId() + "-" + item.getColorCodeId());        viewHolder.brand_tv.setText(item.getBrandName());        viewHolder.shipcode_tv.setText(item.getShipingid());        viewHolder.item_status.setText(item.getStatusText());        viewHolder.qnty.setText("Qty(mtr):"+item.getQuantity());        viewHolder.cost_mtr.setText("Cost(mtr):"+item.getCostPrice());        viewHolder.cost_yrd.setText("Cost(yard):"+item.getCostPrice());        String qnty_yard = String.valueOf((Integer.parseInt(item.getQuantity())*1.19));        viewHolder.rqstd_qnty.setText("Qty(yard):"+qnty_yard);    }    @Override    public int getItemCount() {        return items.size();    }    class ViewHolder extends RecyclerView.ViewHolder {        @BindView(R.id.item_id)        TextView item_code;        @BindView(R.id.brand_tv)        TextView brand_tv;        @BindView(R.id.shipcode_tv)        TextView shipcode_tv;        @BindView(R.id.item_status)        TextView item_status;        @BindView(R.id.qnty)        TextView qnty;        @BindView(R.id.rqstd_qnty)        TextView rqstd_qnty;        @BindView(R.id.cost_mtr)        TextView cost_mtr;        @BindView(R.id.cost_yrd)        TextView cost_yrd;        public ViewHolder(@NonNull View itemView) {            super(itemView);            ButterKnife.bind(this, itemView);        }    }}