package com.obpoo.gfsfabricsoftware.Report.UI;import android.content.Intent;import android.os.Bundle;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.support.v7.widget.Toolbar;import android.util.Log;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;import com.obpoo.gfsfabricsoftware.PurchaseOrder.Adapter.ViewDetailsItemAdapter;import com.obpoo.gfsfabricsoftware.R;import com.obpoo.gfsfabricsoftware.Report.Adapter.ViewDetailRPOAdapter;import com.obpoo.gfsfabricsoftware.Report.DataModel.PurchaseOrderDetails.PoDetailsItems;import com.obpoo.gfsfabricsoftware.ui.activities.BaseActivity;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.ArrayList;import butterknife.BindView;import butterknife.ButterKnife;public class ViewDetailsReportPO extends BaseActivity {    @BindView(R.id.factory_tv)    TextView factory_tv;    @BindView(R.id.duedate)    TextView duedate;    @BindView(R.id.deiverdate)    TextView deiverdate;    @BindView(R.id.stafname)    TextView stafname;    @BindView(R.id.cc_email)    TextView cc_email;    @BindView(R.id.recycler_view)    RecyclerView recycler_view;    @BindView(R.id.textView42)    TextView orderNo;    @BindView(R.id.status)    TextView status_tv;     @BindView(R.id.create_date)    TextView create_date;     @BindView(R.id.update_date)    TextView update_date;     @BindView(R.id.fac_email)    TextView fac_email_tv;    ViewDetailRPOAdapter adapter;    String factory, due_date, deliver_date, status, id, staf, cc_mail,fac_email,createdate,updatedate;    ArrayList<PoDetailsItems> items;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_view_details2);        ButterKnife.bind(this);        Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);        toolbar.setTitle("View Details");        setSupportActionBar(toolbar);        enableActionBar(true);        LinearLayoutManager lm = new LinearLayoutManager(ViewDetailsReportPO.this);        lm.setOrientation(LinearLayoutManager.VERTICAL);        recycler_view.setLayoutManager(lm);        recycler_view.setNestedScrollingEnabled(false);        Intent intent = getIntent();        items = intent.getParcelableArrayListExtra("item");        Log.i("ConfirmItem", items.size() + "");        id = intent.getStringExtra("id");        factory = intent.getStringExtra("factory");        status = intent.getStringExtra("status");        due_date = intent.getStringExtra("due_date");        deliver_date = intent.getStringExtra("deliver_date");        createdate = intent.getStringExtra("create_date");        updatedate = intent.getStringExtra("update");        staf = intent.getStringExtra("staf");        cc_mail = intent.getStringExtra("cc_mail");        fac_email = intent.getStringExtra("fac_mail");        try {            createdate= new SimpleDateFormat("dd-MMM-yyyy").format(new SimpleDateFormat("yyyy-MM-dd").parse(createdate));            updatedate= new SimpleDateFormat("dd-MMM-yyyy").format(new SimpleDateFormat("yyyy-MM-dd").parse(updatedate));        } catch (ParseException e) {            e.printStackTrace();        }        factory_tv.setText(factory);        duedate.setText(due_date);        deiverdate.setText(deliver_date);        status_tv.setText(status);        stafname.setText(staf);        cc_email.setText(cc_mail);        fac_email_tv.setText(fac_email);        orderNo.setText("Order:#" + id);        create_date.setText(createdate);        update_date.setText(updatedate);        adapter = new ViewDetailRPOAdapter(ViewDetailsReportPO.this,items);        recycler_view.setAdapter(adapter);    }}