package com.obpoo.gfsfabricsoftware.TransferStock.Activity;import android.os.Bundle;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.support.v7.widget.Toolbar;import android.text.Editable;import android.text.TextWatcher;import android.widget.EditText;import com.obpoo.gfsfabricsoftware.R;import com.obpoo.gfsfabricsoftware.TransferStock.Adapter.SelectDocAdapter;import com.obpoo.gfsfabricsoftware.ui.activities.BaseActivity;import java.util.ArrayList;import butterknife.BindView;import butterknife.ButterKnife;public class SelectStockDoc extends BaseActivity {    @BindView(R.id.recycler_view)    RecyclerView recycler_view;    @BindView(R.id.etSearch)    EditText etSearch;    SelectDocAdapter adapter;    private ArrayList<String> docList = new ArrayList<>();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_search__select);        ButterKnife.bind(this);        Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);        toolbar.setTitle("Create Request");        setSupportActionBar(toolbar);        enableActionBar(true);        LinearLayoutManager lm = new LinearLayoutManager(this);        lm.setOrientation(LinearLayoutManager.VERTICAL);        recycler_view.setLayoutManager(lm);        docList = getIntent().getStringArrayListExtra("doclist");        adapter = new SelectDocAdapter(SelectStockDoc.this, docList);        recycler_view.setAdapter(adapter);        etSearch.addTextChangedListener(new TextWatcher() {            @Override            public void beforeTextChanged(CharSequence charSequence, int i, int i1, int i2) {            }            @Override            public void onTextChanged(CharSequence charSequence, int i, int i1, int i2) {            }            @Override            public void afterTextChanged(Editable editable) {                    if (docList != null) {                        filter(editable.toString());                    }            }        });    }    void filter(String text) {            ArrayList<String> temp = new ArrayList();            for (String d : docList) {                if (d.toLowerCase().contains(text.toLowerCase())) {                    temp.add(d);                }            }            adapter.updateVendorList(temp);    }}