package com.obpoo.gfsfabricsoftware.TransferStock.Adapter;import android.app.Activity;import android.content.Intent;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.Gravity;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.obpoo.gfsfabricsoftware.PurchaseOrder.UI.POAdd;import com.obpoo.gfsfabricsoftware.PurchaseOrder.UI.Search_Select;import com.obpoo.gfsfabricsoftware.R;import com.obpoo.gfsfabricsoftware.TransferStock.Activity.SelectStockDoc;import com.obpoo.gfsfabricsoftware.TransferStock.Activity.WareHouseToWareHouse;import com.obpoo.gfsfabricsoftware.user.datamodels.UserDetail;import com.obpoo.gfsfabricsoftware.utilities.AppConstants;import com.obpoo.gfsfabricsoftware.vendors.datamodels.VendorsDetail;import java.util.ArrayList;import butterknife.BindView;import butterknife.ButterKnife;public class SelectDocAdapter extends RecyclerView.Adapter<SelectDocAdapter.ViewHolder> {    Activity context;    private ArrayList<String> docList = new ArrayList<>();    public void updateVendorList(ArrayList<String>  list){        docList = list;        notifyDataSetChanged();    }    public SelectDocAdapter(SelectStockDoc context, ArrayList<String> docList) {       this.context = context;        this.docList = docList;    }    @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        View view = LayoutInflater.from(context).inflate(R.layout.searchall_item, viewGroup, false);        SelectDocAdapter.ViewHolder rootView = new SelectDocAdapter.ViewHolder(view);        return rootView;    }    @Override    public void onBindViewHolder(@NonNull final ViewHolder viewHolder, final int i) {        viewHolder.item_name.setGravity(Gravity.CENTER_VERTICAL);            viewHolder.item_name.setText(docList.get(i));        viewHolder.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = new Intent(context, WareHouseToWareHouse.class);                    intent.putExtra("selectdocumentNo", docList.get(i));                    intent.putExtra("selectdocumentPosition", i);                    context.setResult( AppConstants.StockDoc,intent);                    context.finish();            }        });    }    @Override    public int getItemCount() {        return docList.size();    }    class ViewHolder extends RecyclerView.ViewHolder {        @BindView(R.id.item_name)        TextView item_name;        public ViewHolder(@NonNull View itemView) {            super(itemView);            ButterKnife.bind(this, itemView);        }    }}